import { useEffect, useRef } from "react";
import * as THREE from "three";
import Home from "../components/Home";
import {
  LAYOUT_HEIGHT,
  LAYOUT_WIDTH,
  BACKGROUND_COLOR,
} from "../constants/layout";
import { DragControls } from "../shared/Control/DragControls";

const HomePage = () => {
  const layoutRef = useRef(null);
  let camera, scene, renderer;
  let controls, group;
  const objects = [];

  useEffect(() => {
    initializeLayout();
  }, []);

  function initializeCamera() {
    camera = new THREE.PerspectiveCamera(
      50,
      window.innerWidth / window.innerHeight,
      1,
      5000
    );
    camera.position.z = 1000;

    scene = new THREE.Scene();
    scene.background = new THREE.Color(BACKGROUND_COLOR);

    scene.add(new THREE.AmbientLight(0x505050));
    const light = new THREE.SpotLight(0xffffff, 1.5);
    light.position.set(0, 500, 2000);
    light.angle = Math.PI;

    light.castShadow = true;
    light.shadow.camera.near = 1000;
    light.shadow.camera.far = 4000;
    light.shadow.mapSize.width = LAYOUT_WIDTH;
    light.shadow.mapSize.height = LAYOUT_HEIGHT;

    scene.add(light);
  }

  function initializeLayoutObject() {
    group = new THREE.Group();
    scene.add(group);

    const geometry = new THREE.PlaneGeometry(120, 120);

    for (let i = 0; i < 15; i++) {
      const object = new THREE.Mesh(
        geometry,
        new THREE.MeshLambertMaterial({ color: 0x00695c })
      );

      object.position.x = Math.random() * 1000 - 500;
      object.position.y = Math.random() * 600 - 300;

      scene.add(object);
      objects.push(object);
    }
  }

  function initializeLayout() {
    initializeCamera();
    initializeLayoutObject();

    renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(LAYOUT_WIDTH, LAYOUT_HEIGHT);

    renderer.shadowMap.enabled = true;
    renderer.shadowMap.type = THREE.PCFShadowMap;

    layoutRef.current.appendChild(renderer.domElement);

    controls = new DragControls([...objects], camera, renderer.domElement);
    controls.addEventListener("drag", render);
    render();
  }

  function handleOutOfBox() {
    const outOfBoxObjects = scene.children.filter(
      ({ position }) =>
        position.x > LAYOUT_WIDTH ||
        position.x < 0 ||
        position.y > LAYOUT_HEIGHT ||
        position.y < 0
    );
    if(outOfBoxObjects.length){
      console.log(outOfBoxObjects);
    }
  }

  function render() {
    handleOutOfBox();
    renderer.render(scene, camera);
  }

  return <Home layoutRef={layoutRef} />;
};

export default HomePage;
